SELECT ei.index_id, code, NAME, ei.unit_id, di.value, di.data_time, di.time_type FROM energy_index ei LEFT JOIN node_index ni ON ei.index_id = ni.index_id LEFT JOIN data_item di on di.index_id=ei.index_id WHERE ni.node_id = #{nodeId} and ei.code IN
#{indexCode}
and di.time_type = #{timeType} AND di.data_time >= #{beginTime} AND di.data_time <= #{endTime}

SELECT index_id indexId, data_time dataTime, "value" FROM "data_item" WHERE index_id IN
#{indexId}
AND ( data_time >= #{beginTime} AND data_time < #{endTime} ) AND time_type = #{timeType}

SELECT COALESCE (SUM ( "value" ), 0) FROM "data_item" WHERE index_id IN
#{indexId}
AND ( data_time >= #{beginTime} AND data_time < #{endTime} ) AND time_type = #{timeType}

SELECT index_id indexId, data_time dataTime, "value" FROM "data_item" WHERE index_id IN
#{indexId}
AND time_code = #{timeCode}

SELECT COALESCE (SUM ( "value" ), 0) FROM "data_item" WHERE index_id IN
#{indexId}
AND time_code = #{timeCode}

SELECT di.index_id, di.data_time, CASE WHEN di.time_type = 'MONTH' THEN TO_CHAR( di.create_time, 'MM' ) WHEN di.time_type = 'DAY' THEN TO_CHAR( di.create_time, 'DD' ) WHEN di.time_type = 'HOUR' THEN TO_CHAR( di.data_time, 'HH' ) ELSE TO_CHAR( di.data_time, 'YYMMDD' ) END AS dataTimeStr, di.value, ei.energy_id, di.time_type FROM data_item di JOIN energy_index ei ON di.index_id = ei.index_id WHERE di.index_id IN ( SELECT index_id FROM node_index WHERE node_id = #{indexId})
AND ei.energy_id != ''
AND ei.energy_id = #{emissionType}
AND di.data_time BETWEEN #{beginTime} AND #{endTime} AND di.time_type = #{timeType}

SELECT index_id indexId, data_time dataTime, "value" FROM "data_item" WHERE index_id IN
#{indexId}
AND ( begin_time BETWEEN #{beginTime} AND #{endTime} ) AND time_type = #{timeType}
