mapper.select:
select COALESCE(max(order_num), 0)
from model_node
where parent_id = #{parentId}

select count(*)
from model_node
where code = #{code}
  and model_code = #{modelCode};

select count(*)
from model_node
where code = #{code}
  and model_code = #{modelCode}
  AND node_id != #{nodeId};

select ei.index_id as indexId, code, name, index_type as indexType from energy_index ei left join node_index ni on ei.index_id = ni.index_id where ni.node_id = #{nodeId} and ei.index_type = #{indexType}
and code like concat('%', #{code}, '%')
and name like concat('%', #{name}, '%')


where code in

    #{nodeCode}

order by order_num


where model_code = #{modelCode}
and (
node_id in (
select node_id from data_auth_user where user_id = #{userId} and model_code = #{modelCode}
)
or node_id in (
select node_id from data_auth_role where role_id in (
select cast(r.role_id as varchar) from sys_role r
left join sys_user_role ur on ur.role_id = r.role_id
left join sys_user u on u.user_id = ur.user_id
where cast(u.user_id as VARCHAR) = #{userId}
) and model_code = #{modelCode}
)
)
order by order_num

mapper.update:
update model_node
code = #{code}, name = #{name}, parent_id = #{parentId}, address = #{address}, model_code = #{modelCode}, node_category = #{nodeCategory},
where node_id = #{nodeId}

update model_node
set parent_id = #{parentId}
where node_id = #{nodeId};

update model_node
set address = replace(address, (select address from model_node where node_id = #{nodeId}),
                      (select address ||
                              (select code from model_node where node_id = #{nodeId}) ||
                              ','
                       from model_node
                       where node_id = #{parentId}))
where parent_id = #{nodeId}
   or node_id = #{nodeId};

update.foreach:
update model_node
set order_num = #{order}
where node_id = #{id}

mapper.delete:
delete
from node_index
where node_id = #{nodeId}
  and index_id in (select ni.index_id
                   from node_index ni
                            left join energy_index ei on ni.index_id = ei.index_id
                   where node_id = #{nodeId}
                     and ei.index_type = #{indexType});

mapper.select:
SELECT mn.node_id  nodeId,
       mn."name"   nodeName,
       di.index_id indexId,
       di."value"  value
FROM model_node mn
         LEFT JOIN "node_index" ni ON mn.node_id = ni.node_id
         LEFT JOIN "energy_index" ei ON ei.index_id = ni.index_id
         LEFT JOIN "data_item" di ON ei.index_id = di.index_id
WHERE mn.parent_id = #{parentId}
  AND ei.energy_id = #{energyType}
  AND ei.index_type = 'STATISTIC'
  AND di.time_type = #{timeType.name}
  AND di.data_time >= #{dateTimeMap.startTime}
  AND di.data_time <= #{dateTimeMap.endTime}

SELECT mn.node_id  nodeId,
       mn."name"   nodeName,
       di.index_id indexId,
       di."value"  value
FROM "model_node" mn
         LEFT JOIN "node_index" ni ON mn.node_id = ni.node_id
         LEFT JOIN "energy_index" ei ON ei.index_id = ni.index_id
         LEFT JOIN "data_item" di ON ei.index_id = di.index_id
WHERE mn.node_id = #{nodeId}
  AND ei.energy_id = #{energyType}
  AND ei.index_type = 'STATISTIC'
  AND di.time_type = #{timeType.name}
  AND di.data_time >= #{dateTimeMap.startTime}
  AND di.data_time <= #{dateTimeMap.endTime}
