mapper.select:
SELECT index_code indexCode, time_code timeCode, electricity_type electricityType, data_time dataTime, electricity, "cost", time_type timeType, price, remark FROM "electricity_data_item" WHERE index_id IN
#{indexId}
AND (data_time BETWEEN #{startTime} AND #{endTime}) AND time_type = #{timeType}

SELECT index_code indexCode, time_code timeCode, electricity_type electricityType, data_time dataTime, electricity, "cost", time_type timeType, price, remark FROM "electricity_data_item" WHERE index_id IN
#{indexId}
AND time_type = #{timeType}

SELECT di.index_code, di.time_code, di.electricity_type, di.data_time, di.electricity, di.cost, di.time_type, di.price, di.remark FROM "electricity_data_item" di JOIN energy_index ei ON di.index_id = ei.index_id WHERE di.index_id IN ( SELECT index_id FROM node_index WHERE node_id = #{indexId})
AND ei.energy_id != ''
AND ei.energy_id = #{emissionType}
AND (di.data_time BETWEEN #{beginTime} AND #{endTime}) AND di.time_type = #{timeType}
